import React, { useState } from 'react';
import axios from 'axios';
import './CreateProduct.css';
//import router from '../../../car-management-backend/src/utils/cloudinary';

const ProductCreation = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  //const API_URL = process.env.REACT_APP_API_URL;

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title || !description || !image) {
      setMessage('Please fill all fields.');
      return;
    }

    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      console.log(token)

      // Step 1: Upload the image to Cloudinary via the backend
      const formData = new FormData();
      formData.append('image', image);
      console.log("image received",image)

      const uploadResponse = await axios.post(
        'http://localhost:8000/api/upload',
        formData,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'multipart/form-data',
          },
        }
      );

      // const uploadResponse = await axios.post(
      //   `${API_URL}/api/upload`,
      //   formData,
      //   {
      //     headers: {
      //       'Authorization': `Bearer ${token}`,
      //       'Content-Type': 'multipart/form-data',
      //     },
      //   }
      // );

      // Get the image URL from the Cloudinary upload response
     // console.log(uploadResponse.data.imageUrl)
      const imageUrl = uploadResponse.data.imageUrl;
      
      

      // Step 2: Submit the product data with the image URL
      const productData = {
        title,
        description,
        images : [imageUrl],
      };

      const response = await axios.post(
        'http://localhost:8000/api/cars/create',
        productData,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        }
      );

      // const response = await axios.post(
      //   `${API_URL}/api/cars/create`,
      //   productData,
      //   {
      //     headers: {
      //       'Authorization': `Bearer ${token}`,
      //     },
      //   }
      // );

      setLoading(false);
      setMessage('Product created successfully!');
      console.log(response.data);
    } catch (error) {
      setLoading(false);
      setMessage('Error creating product.');
      console.error(error);
    }
  };

  return (
    <div className="product-creation-page">
      <h1>Create a New Product</h1>
      <form onSubmit={handleSubmit} className="product-form">
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter product title"
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter product description"
          />
        </div>

        <div className="form-group">
          <label htmlFor="image">Image</label>
          <input
            type="file"
            id="image"
            accept="image/*"
            onChange={(e) => setImage(e.target.files[0])}
          />
        </div>

        {message && <div className="message">{message}</div>}

        <button type="submit" className="submit-btn" disabled={loading}>

          {loading ? 'Submitting...' : 'Create Product'}
      
        </button>
      </form>
    </div>
  );
};

export default ProductCreation;
